/**
 * Universal CSS for resume rendering.
 * This is used for both the frontend HTML preview and the Puppeteer PDF export.
 */
export declare const RESUME_STYLES = "\n  :root {\n    --resume-base-font: 'Times New Roman', Times, serif;\n    --resume-base-size: 10pt;\n    --resume-line-height: 1.4;\n    --resume-margin: 0.5in;\n  }\n\n  .resume-container {\n    box-sizing: border-box;\n    width: 100%;\n    margin: 0 auto;\n    font-family: var(--resume-base-font);\n    font-size: var(--resume-base-size);\n    line-height: var(--resume-line-height);\n    color: #000;\n    background-color: #fff;\n    padding: var(--resume-margin);\n    min-height: 11in;\n  }\n\n  .resume-header {\n    text-align: center;\n    margin-bottom: 12pt;\n  }\n\n  .resume-name {\n    font-size: 18pt;\n    font-weight: bold;\n    margin: 0 0 4pt 0;\n    text-transform: uppercase;\n    letter-spacing: 1pt;\n  }\n\n  .resume-contact {\n    font-size: 9pt;\n    color: #333;\n    display: flex;\n    justify-content: center;\n    flex-wrap: wrap;\n    gap: 4pt;\n  }\n\n  .separator {\n    margin: 0 2pt;\n  }\n\n  .resume-link {\n    color: #333;\n    text-decoration: none;\n  }\n\n  .resume-section {\n    margin-bottom: 10pt;\n  }\n\n  .section-title {\n    font-size: 11pt;\n    font-weight: bold;\n    text-transform: uppercase;\n    border-bottom: 1pt solid #000;\n    padding-bottom: 2pt;\n    margin-bottom: 6pt;\n    letter-spacing: 0.5pt;\n  }\n\n  .resume-entry {\n    margin-bottom: 8pt;\n  }\n\n  .entry-header {\n    display: flex;\n    justify-content: space-between;\n    font-weight: bold;\n    font-size: 10.5pt;\n    margin-bottom: 1pt;\n  }\n\n  .entry-subheader {\n    display: flex;\n    justify-content: space-between;\n    font-style: italic;\n    font-size: 10pt;\n    color: #444;\n    margin-bottom: 2pt;\n  }\n\n  .entry-description {\n    font-size: 10pt;\n    margin-bottom: 4pt;\n    white-space: pre-wrap;\n  }\n\n  .resume-bullets {\n    margin: 0;\n    padding-left: 15pt;\n    list-style-type: disc;\n  }\n\n  .bullet-item {\n    margin-bottom: 2pt;\n    font-size: 10pt;\n  }\n\n  .skills-grid {\n    display: flex;\n    flex-direction: column;\n    gap: 2pt;\n  }\n\n  .skills-category {\n    font-size: 10pt;\n  }\n\n  .category-name {\n    font-weight: bold;\n  }\n\n  /* PDF-specific adjustments */\n  @media print {\n    body {\n      margin: 0;\n      padding: 0;\n    }\n    .resume-container {\n      padding: var(--resume-margin);\n      box-shadow: none;\n    }\n  }\n\n  /* Paper Preview (for browser UI) */\n  .resume-paper {\n    background: white;\n    box-shadow: 0 0 10px rgba(0,0,0,0.1);\n    width: 8.5in;\n    min-height: 11in;\n    margin: 2rem auto;\n  }\n";
